import {
	Base64
} from 'js-base64';
import JsEncrypt from 'jsencrypt';
import {
	JsonToString,
	StringToJson
} from '@/utils/tool';

/*
 * 加密公钥
 */
let publicKey='MIGfMA0GCSqGSIb3DQEBAQUAA4GNADCBiQKBgQDlOJu6TyygqxfWT7eLtGDwajtNFOb9I5XRb6khyfD1Yt3YiCgQWMNW649887VGJiGr/L5i2osbl8C9+WJTeucF+S76xFxdU6jE0NQ+Z+zEdhUTooNRaY5nZiu5PgDB0ED/ZKBUSLKL7eibMxZtMlUDHjm4gwQco1KRMDSmXSMkDwIDAQAB';

/*
 * 解密私钥
 */
let privateKey='';

export default {

	/*
	 * 加密
	 * @pbk 加密公钥
	 */
	encrypt(value,pbk){
		let encrypt=new JSEncrypt();
        encrypt.setPublicKey(pbk||publicKey);
        return encrypt.encrypt(Base64.encode(JsonToString(value)));
	},

	/*
	 * 解密
	 * @prk 解密私钥
	 */
	decrypt(value,prk){
		let decrypt=new JSEncrypt();
        decrypt.setPrivateKey(prk||privateKey);
        return StringToJson(Base64.decode(decrypt.decrypt(value)));
	}
}
